import axios from "axios";

const link_api = process.env.REACT_APP_API_URL;

export const fetchUsers = async () => {
  const res = await axios.get(link_api);
  return res.data;
};

export const deleteUser = async (id) => {
  await axios.delete(`${link_api}/${id}`);
};

export const updateUser = async (id, user) => {
  await axios.put(`${link_api}/${id}`, user);
};

export const createUser = async (user) => {
  await axios.post(link_api, user);
};

export const toggleUserActive = async (id) => {
  await axios.put(`${link_api}/${id}/toggle-active`);
};
