import React, { useEffect, useState } from "react";
import UserTable from "../components/UserTable";
import { fetchUsers, deleteUser, updateUser, createUser } from "../services/userService";
import UserForm from "../components/UserForm";

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [ediitingUser, setEditingUser] = useState(null);

  const loadUsers = async () => {
    const data = await fetchUsers();
    setUsers(data);
  };

  useEffect(() => {
    loadUsers();
  }, []);

  const handleDelete = async (id) => {
    await deleteUser(id);
    loadUsers();
  };

  const handleEdit =  async (user) => {
   setEditingUser(user);
   console.log("get id: "+ user);
   setShowForm(true);
  };

   const handleCreateUser = async() => {
    setEditingUser(null);
    setShowForm(true);
   }

   const handleSubmitForm = async (formData) => {
       if(ediitingUser){
        await updateUser(formData.id, formData);
       }
       else await createUser();
       setShowForm(false);
   }

   const handleLockUser = async (id) => {
       console.log('develop after');
       alert("Current develop")
   };
  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Users Management</h1>
       {!showForm && (
        <button onClick={handleCreateUser} className="mb-4 bg-green-500 text-white px-4 py-2 rounded">
           Add User
        </button>
      )}
       {showForm ? (
        <UserForm  initialData ={ediitingUser} onSubmit={handleSubmitForm} onCancel={() => setShowForm(false) }/>
       ): 
        <UserTable users={users} onEdit={handleEdit} onDelete={handleDelete} onLock={handleLockUser} />
        }

    </div>
  );
};

export default UserManagement;
