// src/components/UserForm.jsx
import React, { useState, useEffect } from 'react';

const UserForm = ({ initialData = {}, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
   username: '',
    full_name: '',
    email: '',
    role: '',
    phone_number: '',
    gender: '',
    date_of_birth: '',
  });

  useEffect(() => {
    if (initialData && Object.keys(initialData).length > 0) {
      setFormData(initialData);
    }
  }, [initialData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 bg-white p-4 rounded-lg shadow-md">
         <div>
        <label className="block mb-1 font-medium">Username</label>
        <input type="text" name="username" value={formData.username} onChange={handleChange}
          className="w-full border px-3 py-2 rounded" required />
      </div>
      <div>
        <label className="block mb-1 font-medium">Full Name</label>
        <input type="text" name="full_name" value={formData.full_name} onChange={handleChange}
          className="w-full border px-3 py-2 rounded" required />
      </div>

      <div>
        <label className="block mb-1 font-medium">Email</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange}
          className="w-full border px-3 py-2 rounded" required />
      </div>

      <div>
        <label className="block mb-1 font-medium">Role</label>
        <input type="text" name="role" value={formData.role} onChange={handleChange}
          className="w-full border px-3 py-2 rounded" required />
      </div>
  <div>
        <label className="block mb-1 font-medium">Phone </label>
        <input type="text" name="phone_number" value={formData.phone_number} onChange={handleChange}
          className="w-full border px-3 py-2 rounded" required />
      </div>
  <div>
        <label className="block mb-1 font-medium">Gender</label>
        <input type="text" name="gender" value={formData.gender} onChange={handleChange}
          className="w-full border px-3 py-2 rounded" required />
      </div>
      <div>
        <label className="block mb-1 font-medium">Birthday</label>
        <input type="date" name="date_of_birth" value={formData.date_of_birth} onChange={handleChange}
          className="w-full border px-3 py-2 rounded" required />
      </div>

      <div className="flex justify-end gap-2">
        <button type="button" onClick={onCancel} className="bg-gray-300 px-4 py-2 rounded">Huỷ</button>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
          {initialData?.id ? 'Cập nhật' : 'Tạo mới'}
        </button>
      </div>
    </form>
  );
};

export default UserForm;
