import axios from "axios";

const api_link = process.env.API_URL;

export const fetchUsers = async () => {
  const res = await axios.get(`${api_link}/users`);
  return res.data;
};

export const deleteUser = async (id) => {
  await axios.delete(`${api_link}/users/${id}`);
};
export const updateUser = async (id, user) => {
  await axios.put(`${api_link}/users/${id}`, user);
}
export const createUser = async(user) =>{
    await axios.post(`${api_link}/users`, user);
}

export const toggleUserActive = async(id) => {
  await axios.put(`${api_link}/users/${id}/toggle-active`);
};
